window.templates={"any":{"any.context.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  var map = context.makeContextMap(res);\n\n  writer.start('application/json');\n  writer.write(JSON.stringify(map, null, 2));\n  writer.end();\n});\n"},"any.default.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  writer.start('text/html');\n\n  writer.write('name:' + res.getName());\n  writer.write('<h1>render types:</h1>');\n  writer.write('<ul>');\n\n  var types = res.getRenderTypes();\n  for (var i = 0; i < types.length; i++) {\n    writer.write('<li>' + types[i] + '</li>');\n  }\n  writer.write('</ul>');\n\n\n  writer.write('<h1>properties:</h1>');\n  writer.write('<ul>');\n\n  var names = res.getPropertyNames();\n  for (var i = 0; i < names.length; i++) {\n    var name = names[i];\n    var val = res.getProperty(name);\n    writer.write('<li>' + name + '=' + val + '</li>');\n  }\n\n  writer.write('</ul>');\n\n  var w = writer.makeNestedContentWriter();\n  res.listChildrenNames(function (children) {\n    w.write('<h1>children:</h1>');\n    w.write('<ul>');\n    for (var i = 0; i < children.length; i++) {\n      var child = children[i];\n      w.write('<li>' + child + '</a></li>');\n    }\n    w.write('</ul>');\n    w.end();\n  });\n\n  writer.end();\n})\n"},"any.json.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer) {\n  writer.start('application/json');\n  writer.write(JSON.stringify(res.getProperties(), null, 2));\n  writer.end();\n});\n"},"any.store.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  res.values['modified_date'] = '' + new Date();\n\n  writer.write(res);\n  writer.end();\n});\n"}},"mime":{"text":{"x-markdown":{"html.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  var adapter = new ContentWriterAdapter('utf8', function (txt) {\n    writer.start('text/html');\n\n    var md = null;\n    var result = '';\n\n    if (window && typeof window['markdownit'] !== 'undefined') {\n      md = window.markdownit();\n    }\n    else {\n      md = require('markdown-it')();\n    }\n\n    if (txt) result = md.render(txt);\n\n    writer.write(result);\n    writer.end();\n  });\n\n  res.read(adapter);\n});\n"}}}},"resource":{"content":{"content.default.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  res.read(writer);\n});\n"},"content.edit.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n  <head>\n    <meta charset=\"utf-8\">\n    <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0, maximum-scale=1.0\">\n    <title>edit</title>\n\n    <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n    <link href=\"{{C.CODEMIRROR_CSS}}\" rel=\"stylesheet\">\n    <link href=\"{{C.CODEMIRROR_THEME}}\" rel=\"stylesheet\">\n    <script src=\"{{C.CODEMIRROR_JS}}\"></script>\n    <style>\n      #editor-container {\n          margin-top: 20px;\n        }\n        #editor-container .CodeMirror {\n          position: fixed;\n          bottom: 0px;\n          top: 100px;\n          right: 0px;\n          left: 0px;\n          font-size: 11px;\n          line-height: 11px;\n          height: initial;\n        }\n        #editor-container img {\n          max-width: fit-content;\n          width: 100%;\n        }\n      </style>\n  </head>\n\n  <body>\n    <div class=\"container\">\n\n      <ol class=\"breadcrumb\">\n        <li><a href=\"/{{C.X}}res-list\">HOME</a></li>\n        {{#include \".\" \"parents\"}}\n          <li><a href=\"{{path}}{{C.X}}res-list\">{{name}}</a></li>\n        {{/include}}\n        <li class=\"active\">{{R.NAME}}</li>\n      </ol>\n\n      {{#match isTextContentResource}}\n        <form method=\"post\" action=\"{{R.PATH}}{{R.SUFFIX}}\">\n\n          <div class=\"btn-group btn-group-sm\">\n            <a class=\"btn btn-default\" href=\"{{R.PATH}}/../{{C.X}}res-list\"><span class=\"glyphicon glyphicon-th-list\"></span> List</a>\n            <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-properties\"><span class=\"glyphicon glyphicon-edit\"></span> Properties</a>\n          </div>\n          <div class=\"btn-group btn-group-sm pull-right\">\n            <button class=\"btn btn-default\" type=\"submit\"><span class=\"glyphicon glyphicon-floppy-save\"></span> Save</button>\n          </div>\n\n          <div id=\"editor-container\">\n            <textarea autocorrect=\"off\" autocapitalize=\"none\" autocomplete=\"off\" autofocus=\"on\" spellcheck=\"false\" name=\"_content\">\n{{include-safe \".\" \"text\"}}</textarea>\n          </div>\n          <input type=\"hidden\" name=\"_rt\" value=\"resource/content\"></input>\n          <input type=\"hidden\" name=\"_ct\" value=\"{{contentType}}\"></input>\n        </form>\n      {{/match}}\n      {{#match contentType \"startsWith\" \"image/\"}}\n\n        <div class=\"btn-group btn-group-sm\">\n          <a class=\"btn btn-default\" href=\"{{R.PATH}}/../{{C.X}}res-list\"><span class=\"glyphicon glyphicon-th-list\"></span> List</a>\n          <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-properties\"><span class=\"glyphicon glyphicon-edit\"></span> Properties</a>\n          <a class=\"btn btn-default\" target=\"_blank\" href=\"{{include \".\" \"externalize\"}}\"><span class=\"glyphicon glyphicon-eye-open\"></span> View</a>\n        </div>\n\n        <div id=\"editor-container\">\n          <img class=\"image\" src=\"{{include \".\" \"externalize\"}}\"></img>\n        </div>\n      {{/match}}\n      {{#match contentType \"startsWith\" \"application/pdf\"}}\n\n        <div class=\"btn-group btn-group-sm\">\n          <a class=\"btn btn-default\" href=\"{{R.PATH}}/../{{C.X}}res-list\"><span class=\"glyphicon glyphicon-th-list\"></span> List</a>\n          <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-properties\"><span class=\"glyphicon glyphicon-edit\"></span> Properties</a>\n          <a class=\"btn btn-default\" target=\"_blank\" href=\"{{include \".\" \"externalize\"}}\"><span class=\"glyphicon glyphicon-eye-open\"></span> View</a>\n        </div>\n\n        <div id=\"editor-container\">\n          <iframe class=\"pdf\" src=\"{{include \".\" \"externalize\"}}\"></iframe>\n        </div>\n      {{/match}}\n\n      {{#default}}\n        unknown file type of {{contentType}} <br>\n        <a href=\"{{include \".\" \"externalize\"}}\" download target=\"_blank\">download</a>\n      {{/default}}\n    </div>\n    <script>\n      setTimeout(function() {\n        var ta = document.querySelector('textarea');\n        if (ta) {\n          var editor = CodeMirror.fromTextArea(ta, {\n            lineNumbers: true,\n            autofocus: true,\n            mode: 'javascript',\n            theme: 'solarized dark'\n          });\n        }\n      },1);\n    </script>\n  </body>\n\n</html>\n"},"content.externalize.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  var clientside = (typeof window != 'undefined');\n\n  if (clientside) {\n    if (res.isContentResource()) {\n      res.read(new ContentWriterAdapter('blob', function (data, ctype) {\n        var blob = new Blob([data], {\n          type: ctype ? ctype : 'application/octet-binary'\n        });\n        var url = URL.createObjectURL(blob);\n\n        writer.start('text/plain');\n        writer.write(url);\n        writer.end();\n      }));\n    }\n    else {\n      writer.error(new Error('resource has no content'));\n      writer.end();\n    }\n  }\n  else {\n    var url = context.pathInfo.resourcePath;\n    writer.start('text/plain');\n    writer.write(url);\n    writer.end();\n  }\n});\n"},"content.res-list.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  context.forwardRequest(context.getCurrentResourcePath() + '.x-edit');\n});\n"},"content.text.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  if (res.isContentResource()) {\n    res.read(new ContentWriterAdapter('utf8', function (data, ctype) {\n      writer.start('text/plain');\n      writer.write(data);\n      writer.end();\n    }));\n  }\n  else {\n    writer.error(new Error('resource has no content'));\n    writer.end();\n  }\n});\n"}},"node":{"node.children.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  writer.start('object/javascript');\n\n  res.listChildrenResources(function (children) {\n    var rv = [];\n    children.sort(function(a, b) {\n      return a.getName().localeCompare(b.getName());\n    });\n\n    for (var i = 0; i < children.length; i++) {\n      var res = children[i];\n      var map = context.makeContextMap(res);\n      var name = res.getName();\n\n      map['name'] = name;\n      map['path'] = Utils.filename_path_append(context.getCurrentResourcePath(), name);\n\n      rv.push(map);\n    }\n\n    writer.write(rv);\n    writer.end();\n  });\n\n})\n"},"node.export.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  var out = writer;\n  var processing = 0;\n  var ended = false;\n  var count = 0;\n  var path = context.getCurrentDataPath();\n\n  var export_content = function (data) {\n    var func = data['_content'];\n    var ct = data['_ct'];\n    var bin = true;\n\n    if (Utils.is_texttype(ct)) bin = false;\n\n    func(new ContentWriterAdapter(bin ? null : 'utf8', function (buff) {\n\n      if (bin) {\n        data['_ct'] = 'base64:' + (ct ? ct : '');\n        data['_content'] = Utils.ArrayBuffer2base64(buff);\n      }\n      else data['_content'] = buff;\n\n      if (count > 0) out.write('\\n,\\n');\n      out.write(JSON.stringify(data, null, 2));\n\n      processing--;\n      done();\n    }));\n  };\n\n  var done = function () {\n    if (processing === 0 && ended) {\n      out.write('\\n]');\n      out.end();\n      if (out !== writer) writer.end();\n    }\n  };\n\n  var export_children = function () {\n    res.exportChilrenResources(0, {\n      start: function (ctype) {\n        out.start('application/json');\n        out.write('[\\n');\n      },\n      write: function (data) {\n\n        if (data.values['_content']) {\n          processing++;\n          export_content(data.values);\n        }\n        else {\n          if (count > 0) out.write('\\n,\\n');\n          out.write(JSON.stringify(data.values, null, 2));\n        }\n        count++;\n      },\n      error: function (err) {\n        console.log(err);\n      },\n      end: function () {\n        ended = true;\n        done();\n      }\n    }, true);\n  };\n\n  if (path) {\n    context.resolveResource(path, function(ores) {\n      if (ores) {\n        out = ores.getWriter();\n        export_children();\n      }\n      else {\n        context.storeAndResolveResource(path, {'_content':''}, function(ores) {\n          out = ores.getWriter();\n          export_children();\n        });\n      }\n    });\n  }\n  else {\n    export_children();\n  }\n\n});\n"},"node.load.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  var rt = Utils.filename_dir(res.getRenderType());\n  var dpath = context.getCurrentDataPath();\n  var path = Utils.filename_path_append(rt, dpath);\n\n  context.resolveTemplateResource(path, function (res) {\n    if (res) {\n      res.read(writer);\n    }\n    else {\n      writer.start('text/html');\n      writer.write('not found: ' + path);\n      writer.end();\n    }\n  });\n})\n"},"node.parents.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  writer.start('object/javascript');\n\n  var path = context.getCurrentResourcePath();\n\n  var parentPaths = [];\n  var ps = Utils.split_path(path);\n  ps.pop();\n\n  while (ps.length > 0) {\n    var rpath = ps.join('/');\n    var name = ps.pop();\n    var map = context.makeContextMap(res);\n\n    if (name === '') continue;\n\n    map['path'] = '/' + rpath;\n    map['name'] = name;\n\n    parentPaths.unshift(map);\n  }\n\n  writer.write(parentPaths);\n  writer.end();\n\n});\n"},"node.properties.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  writer.start('object/javascript');\n  var rv = [];\n\n  var names = res.getPropertyNames();\n  var props = context.getRequestProperties();\n\n  names.sort(function(a, b) {\n    return a.localeCompare(b);\n  });\n\n  for (var i = 0; i < names.length; i++) {\n    var name = names[i];\n    var val = res.getProperty(name);\n\n    rv.push({\n      name: name,\n      value: val,\n      R: props\n    });\n  }\n\n  writer.write(rv);\n  writer.end();\n\n})\n"},"res-copyto.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>copy to</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Copy Resource</h4>\n        </div>\n        <div class=\"modal-body\">\n\n          <h1>Copy from</h1>\n\n          <ol class=\"breadcrumb\">\n            <li class=\"active\">HOME</li>\n            {{#include \".\" \"parents\"}}\n              <li class=\"active\">{{name}}</li>\n            {{/include}}\n            <li>{{R.NAME}}</li>\n          </ol>\n\n          <h1>to</h1>\n\n          <ol class=\"breadcrumb\">\n            <li><a href=\"{{R.PATH}}{{C.X}}res-copyto/\">HOME</a></li>\n            {{#include R.DATA_PATH \"parents\"}}\n              <li><a href=\"{{R.PATH}}{{C.X}}res-copyto{{path}}\">{{name}}</a></li>\n            {{/include}}\n            <li class=\"active\">{{R.DATA_NAME}}</li>\n          </ol>\n\n          <form method=\"post\" action=\"{{R.PATH}}\">\n            <table class=\"table\">\n              {{#include R.DATA_PATH \"children\"}}\n                {{#if isContentResource}}\n                  <tr>\n                    <td>\n                      <i class=\"glyphicon glyphicon-file\"></i> {{name}}\n                    </td>\n                  </tr>\n                {{else}}\n                  <tr>\n                    {{#match path \"!startsWith\" R.PATH}}\n                      <td>\n                        <i class=\"glyphicon glyphicon-folder-close\"></i> <a href=\"{{R.PATH}}{{C.X}}res-copyto{{path}}\">{{name}}</a>\n                      </td>\n                    {{/match}}\n                  </tr>\n                {{/if}}\n\n              {{/include}}\n              <tr>\n                <td>\n                  <input class=\"form-control\" name=\":name|newUUID\" value=\"Copy of {{R.NAME}}\" autocorrect=\"off\" autocapitalize=\"off\" autocomplete=\"off\" spellcheck=\"false\">\n                </td>\n              </tr>\n\n            </table>\n\n            <input type=\"hidden\" name=\":copyto\" value=\"{{R.DATA_PATH_APPEND}}{:name}\">\n            <input type=\"hidden\" name=\":forward\" value=\"{{R.DATA_PATH}}{{C.X}}res-list\">\n\n            <div class=\"modal-footer\">\n              <div class=\"pull-left\">\n                <a class=\"btn btn-default\" target=\"_blank\" href=\"{{R.PATH}}{{C.X}}export\">Export</a>\n                <a class=\"btn btn-default\" target=\"_blank\" href=\"{{R.PATH}}{{C.X}}res-exportto{{R.DATA_PATH}}\">Export to...</a>\n              </div>\n              <a class=\"btn btn-default\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\">Cancel</a>\n              <input class=\"btn btn-success\" type=\"submit\" value=\"Copy\">\n            </div>\n\n          </form>\n        </div><!-- /.modal-content -->\n      </div><!-- /.modal-dialog -->\n    </div><!-- /.modal -->\n\n</body>\n\n</html>\n"},"res-create.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>create</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Create New Resource</h4>\n        </div>\n        <div class=\"modal-body\">\n\n\n          <ol class=\"breadcrumb\">\n            <li class=\"active\">HOME</li>\n            {{#include \".\" \"parents\"}}\n              <li class=\"active\">{{name}}</li>\n            {{/include}}\n            <li>{{R.NAME}}</li>\n          </ol>\n\n          <h5>Resource Node</h5>\n          <form class=\"form-inline\" method=\"post\" action=\"{{R.PATH_APPEND}}{:name}\">\n            <div class=\"form-group\">\n              <select class=\"form-control\" name=\"_rt\">\n                <option value=\"\">not set</option>\n                <option disabled>-</option>\n                {{#include C.USER_TEMPLATES \"rendertemplates\"}}\n                  <option value=\"{{this}}\">{{this}}</option>\n                {{/include}}\n              </select>\n\n              <input class=\"form-control\" name=\":name|newUUID\" value=\"\" autocomplete=\"off\" placeholder=\"name\">\n              <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH}}{{C.X}}res-list\">\n              <button class=\"form-control btn btn-success\" type=\"submit\">\n                <span class=\"glyphicon glyphicon-plus\"></span>\n              </button>\n            </div>\n          </form>\n\n          <h5>Create Empty File</h5>\n\n          <form class=\"form-inline\" method=\"post\" action=\"{{R.PATH_APPEND}}{:name}\">\n            <div class=\"form-group\">\n              <input class=\"form-control\" name=\":name|newUUID\" value=\"\" autocomplete=\"off\" placeholder=\"file name\">\n              <input type=\"hidden\" name=\"_content\" value=\"\"></input>\n              <!--input type=\"hidden\" name=\"_ct\" value=\"text/plain\"></input-->\n              <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH}}{{C.X}}res-list\">\n              <button class=\"form-control btn btn-success\" type=\"submit\">\n                <span class=\"glyphicon glyphicon-plus\"></span>\n              </button>\n            </div>\n          </form>\n\n\n          <hr>\n          <h5>Upload File</h5>\n\n          <form class=\"form-inline\" method=\"post\" enctype=\"multipart/form-data\" action=\"{{R.PATH_APPEND}}{:name}\">\n            <div class=\"form-group\">\n              <input class=\"form-control\" type=\"file\" name=\":filename\" value=\"\">\n              <input class=\"form-control\" name=\":name|:filename|newUUID\" value=\"\" autocomplete=\"off\" placeholder=\"file name\">\n              <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH}}{{C.X}}res-list\">\n              <button class=\"form-control btn btn-success\" type=\"submit\">\n                <span class=\"glyphicon glyphicon-plus\"></span>\n              </button>\n\n            </div>\n          </form>\n\n          <h5>Import File</h5>\n          <form class=\"form-inline\" method=\"post\" enctype=\"multipart/form-data\" action=\"{{R.PATH}}\">\n            <div class=\"form-group\">\n              <input class=\"form-control\" type=\"file\" name=\":import\" value=\"\">\n              <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH}}{{C.X}}res-list\">\n\n              <button class=\"form-control btn btn-success\" type=\"submit\">\n                <span class=\"glyphicon glyphicon-plus\"></span>\n              </button>\n            </div>\n          </form>\n\n        </div>\n        <div class=\"modal-footer\">\n          <a class=\"btn btn-default\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\">Cancel</a>\n        </div>\n      </div><!-- /.modal-content -->\n    </div><!-- /.modal-dialog -->\n  </div><!-- /.modal -->\n\n\n</body>\n\n</html>\n"},"res-delete.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>Delete</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Delete Resource</h4>\n        </div>\n        <div class=\"modal-body\">\n\n          <ol class=\"breadcrumb\">\n            <li class=\"active\">HOME</li>\n            {{#include \".\" \"parents\"}}\n              <li class=\"active\">{{name}}</li>\n            {{/include}}\n            <li>{{R.NAME}}</li>\n          </ol>\n\n          <h4>Are you sure you want to delete \"{{name}}\"?</h4>\n\n        </div>\n        <div class=\"modal-footer\">\n          <form class=\"form-inline\" method=\"post\" action=\"{{R.PATH}}\">\n            <a class=\"btn btn-default\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\">Cancel</a>\n            <input type=\"hidden\" name=\":delete\" value=\"{{R.PATH}}\">\n            <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH_APPEND}}../{{C.X}}res-list\">\n            <input class=\"btn btn-danger\" type=\"submit\" value=\"Delete\">\n          </form>\n\n        </div>\n      </div><!-- /.modal-content -->\n    </div><!-- /.modal-dialog -->\n  </div><!-- /.modal -->\n\n</body>\n\n</html>\n"},"res-exportto.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>export to</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Export Resource</h4>\n        </div>\n        <div class=\"modal-body\">\n\n          <h1>Export from</h1>\n\n          <ol class=\"breadcrumb\">\n            <li class=\"active\">HOME</li>\n            {{#include \".\" \"parents\"}}\n              <li class=\"active\">{{name}}</li>\n            {{/include}}\n            <li>{{R.NAME}}</li>\n          </ol>\n\n          <h1>to</h1>\n\n          <ol class=\"breadcrumb\">\n            <li><a href=\"{{R.PATH}}{{C.X}}res-exportto/\">HOME</a></li>\n            {{#include R.DATA_PATH \"parents\"}}\n              <li><a href=\"{{R.PATH}}{{C.X}}res-exportto{{path}}\">{{name}}</a></li>\n            {{/include}}\n            <li class=\"active\">{{R.DATA_NAME}}</li>\n          </ol>\n\n          <form method=\"post\" action=\"{{R.PATH}}{{C.X}}export{{R.DATA_PATH_APPEND}}{:name}\">\n            <table class=\"table\">\n              {{#include R.DATA_PATH \"children\"}}\n                {{#if isContentResource}}\n                  <tr>\n                    <td>\n                      <i class=\"glyphicon glyphicon-file\"></i> {{name}}\n                    </td>\n                  </tr>\n                {{else}}\n                  <tr>\n                    {{#match path \"!startsWith\" R.PATH}}\n                      <td>\n                        <i class=\"glyphicon glyphicon-folder-close\"></i> <a href=\"{{R.PATH}}{{C.X}}res-exportto{{path}}\">{{name}}</a>\n                      </td>\n                    {{/match}}\n                  </tr>\n                {{/if}}\n\n              {{/include}}\n              <tr>\n                <td>\n                  <input class=\"form-control\" name=\":name|newUUID\" value=\"{{R.NAME}}.json\" autocorrect=\"off\" autocapitalize=\"off\" autocomplete=\"off\" spellcheck=\"false\">\n                </td>\n              </tr>\n\n            </table>\n\n            <input type=\"hidden\" name=\":forward\" value=\"{{R.DATA_PATH}}{{C.X}}res-list\">\n\n            <div class=\"modal-footer\">\n              <a class=\"btn btn-default\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\">Cancel</a>\n              <input class=\"btn btn-success\" type=\"submit\" value=\"Export\">\n            </div>\n\n          </form>\n        </div><!-- /.modal-content -->\n      </div><!-- /.modal-dialog -->\n    </div><!-- /.modal -->\n\n</body>\n\n</html>\n"},"res-list-actions.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<div class=\"btn-group btn-group-xs\">\n  <a class=\"btn btn-default glyphicon glyphicon-trash\" href=\"{{path}}{{C.X}}res-delete\"></a>\n  <a class=\"btn btn-default glyphicon glyphicon-duplicate\" href=\"{{path}}{{C.X}}res-copyto{{path}}/..\"></a>\n  <a class=\"btn btn-default glyphicon glyphicon-transfer\" href=\"{{path}}{{C.X}}res-moveto{{path}}/..\"></a>\n</div>\n<div class=\"btn-group btn-group-xs\">\n  {{#if renderType}}\n    <a class=\"btn btn-default glyphicon glyphicon-eye-open\" target=\"_blank\" href=\"{{path}}\"></a>\n  {{/if}}\n  <a class=\"btn btn-default btn-default glyphicon glyphicon-edit\" href=\"{{path}}{{C.X}}res-properties\"></a>\n</div>\n"},"res-list.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"user-scalable=no,width=device-width, initial-scale=1.0\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>list</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div class=\"container\">\n\n    <ol class=\"breadcrumb\">\n      <li><a href=\"/{{R.SUFFIX}}\">HOME</a></li>\n      {{#include \".\" \"parents\"}}\n        <li><a href=\"{{path}}{{R.SUFFIX}}\">{{name}}</a></li>\n      {{/include}}\n      <li class=\"active\">{{R.NAME}}</li>\n    </ol>\n\n    <div style=\"margin-bottom:20px\">\n    <div class=\"btn-group btn-group-sm\">\n      <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-create\"><span class=\"glyphicon glyphicon-plus\"></span> Create</a>\n      <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-renderer\"><span class=\"glyphicon glyphicon-fire\"></span> Renderer</a>\n      <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-properties\"><span class=\"glyphicon glyphicon-edit\"></span> Properties</a>\n    </div>\n\n    <div class=\"btn-group btn-group-sm pull-right\">\n      <a class=\"btn btn-default\" target=\"_blank\" href=\"{{path}}{{C.X}}context\"><span class=\"glyphicon glyphicon-cutlery\"></span> Context Dump</a>\n  {{#if renderType}}\n      <a class=\"btn btn-default\" target=\"_blank\" href=\"{{path}}\"><span class=\"glyphicon glyphicon-eye-open\"></span> Render As \"{{renderType}}\"</a>\n  {{/if}}\n    </div>\n  </div>\n\n    <table class=\"table table-bordered table-condensed small text-muted\" style=\"background-color:lightgray\">\n      {{#include \".\" \"properties\"}}\n        <tr>\n          <td>\n            {{name}}\n          </td>\n          <td>\n            {{value}}\n          </td>\n        </tr>\n      {{/include}}\n    </table>\n\n    <table class=\"table ui-dropzone\">\n      {{#include \".\" \"children\"}}\n\n        {{#if isContentResource}}\n          <tr>\n            <td>\n              <i class=\"glyphicon glyphicon-file\"></i> <a href=\"{{path}}{{C.X}}edit\">{{name}}</a>\n            </td>\n            <td>\n              {{contentType}}\n            </td>\n            <td class=\"text-right\">\n              {{include path \"res-list-actions\"}}\n            </td>\n          </tr>\n        {{else}}\n          <tr>\n            <td>\n              <i class=\"glyphicon glyphicon-folder-close\"></i> <a href=\"{{path}}{{R.SUFFIX}}\">{{name}}</a>\n            </td>\n            <td>\n              {{#if renderType}}\n                <a href=\"{{C.USER_TEMPLATES}}/{{renderType}}{{C.X}}res-list\">{{renderType}}</a>\n              {{else}}\n                {{type}}\n              {{/if}}\n            </td>\n            <td class=\"text-right\">\n              {{include path \"res-list-actions\"}}\n            </td>\n          </tr>\n        {{/if}}\n\n      {{/include}}\n    </table>\n\n  </div>\n</body>\n\n</html>\n"},"res-moveto.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>move to</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Move or Rename Resource</h4>\n        </div>\n        <div class=\"modal-body\">\n\n          <h1>Move from</h1>\n\n          <ol class=\"breadcrumb\">\n            <li class=\"active\">HOME</li>\n            {{#include \".\" \"parents\"}}\n              <li class=\"active\">{{name}}</li>\n            {{/include}}\n            <li>{{R.NAME}}</li>\n          </ol>\n\n          <h1>to</h1>\n\n          <ol class=\"breadcrumb\">\n            <li><a href=\"{{R.PATH}}{{C.X}}res-moveto/\">HOME</a></li>\n            {{#include R.DATA_PATH \"parents\"}}\n              <li><a href=\"{{R.PATH}}{{C.X}}res-moveto{{path}}\">{{name}}</a></li>\n            {{/include}}\n            <li class=\"active\">{{R.DATA_NAME}}</li>\n          </ol>\n\n          <form method=\"post\" action=\"{{R.PATH}}\">\n\n            <table class=\"table\">\n              {{#include R.DATA_PATH \"children\"}}\n                {{#if isContentResource}}\n                  <tr>\n                    <td>\n                      <i class=\"glyphicon glyphicon-file\"></i> {{name}}\n                    </td>\n                  </tr>\n                {{else}}\n                  <tr>\n                    {{#match path \"!startsWith\" R.PATH}}\n                      <td>\n                        <i class=\"glyphicon glyphicon-folder-close\"></i> <a href=\"{{R.PATH}}{{C.X}}res-moveto{{path}}\">{{name}}</a>\n                      </td>\n                    {{/match}}\n                  </tr>\n                {{/if}}\n\n              {{/include}}\n              <tr>\n                <td>\n                  <input class=\"form-control\" name=\":name|newUUID\" value=\"{{R.NAME}}\" autocomplete=\"off\">\n                </td>\n              </tr>\n            </table>\n\n\n            <div class=\"modal-footer\">\n              <input type=\"hidden\" name=\":moveto\" value=\"{{R.DATA_PATH_APPEND}}{:name}\">\n              <input type=\"hidden\" name=\":forward\" value=\"{{R.DATA_PATH}}{{C.X}}res-list\">\n              <a class=\"btn btn-default\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\">Cancel</a>\n              <input class=\"btn btn-success\" type=\"submit\" value=\"Move\">\n            </div>\n\n          </form>\n        </div><!-- /.modal-content -->\n      </div><!-- /.modal-dialog -->\n    </div><!-- /.modal -->\n\n\n</body>\n\n</html>\n"},"res-properties.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0, maximum-scale=1.0\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>properties</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH}}{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Resource Properties</h4>\n        </div>\n        <div class=\"modal-body\">\n\n          <ol class=\"breadcrumb\">\n            <li class=\"active\">HOME</li>\n            {{#include \".\" \"parents\"}}\n              <li class=\"active\">{{name}}</li>\n            {{/include}}\n            <li>{{R.NAME}}</li>\n          </ol>\n\n          <h4>properties:</h4>\n\n          {{#include \".\" \"properties\"}}\n            <div class=\"form-horizontal\">\n              <div class=\"form-group\">\n                <label class=\"col-sm-2\">{{name}}</label>\n                <div class=\"col-sm-8\">\n                  <input class=\"form-control\" name=\"{{name}}\" value=\"{{value}}\" form=\"save_form\" autocorrect=\"off\" autocapitalize=\"none\" autocomplete=\"off\" spellcheck=\"false\">\n                </div>\n                <div class=\"col-sm-2\">\n                  <form method=\"POST\" action=\"{{R.PATH}}{{R.SUFFIX}}\">\n                    <input type=\"hidden\" name=\"{{name}}\" value=\"\">\n                    <button class=\"btn btn-danger\" type=\"submit\" name=\"{{name}}\" value=\"\">\n                      <span class=\"glyphicon glyphicon-minus\"></span>\n                    </button>\n                  </form>\n                </div>\n              </div>\n            </div>\n          {{/include}}\n\n          <form class=\"form-horizontal\" method=\"post\" action=\"{{R.PATH}}{{R.SUFFIX}}\">\n            <div class=\"form-group\">\n              <div class=\"col-sm-2\">\n                <input class=\"form-control\" name=\":name\" value=\"\" autocorrect=\"off\" autocapitalize=\"none\" autocomplete=\"off\" spellcheck=\"false\" placeholder=\"name\">\n              </div>\n              <div class=\"col-sm-8\">\n                <input class=\"form-control\" name=\"{:name}\" value=\"\" autocorrect=\"off\" autocapitalize=\"none\" autocomplete=\"off\" spellcheck=\"false\" placeholder=\"value\">\n              </div>\n              <div class=\"col-sm-2\">\n                <button class=\"btn btn-success\" type=\"submit\" value=\"add\">\n                  <span class=\"glyphicon glyphicon-plus\"></span>\n                </button>\n              </div>\n            </div>\n          </form>\n\n      </div>\n      <div class=\"modal-footer\">\n        <div class=\"pull-left\">\n          <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-renderer\">Renderer Properties...</a>\n        </div>\n\n        <form class=\"form-horizontal\" class=\"form-inline\" method=\"post\" id=\"save_form\" action=\"{{R.PATH}}{{C.X}}res-list\">\n          <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-list\">Cancel</a>\n          <input class=\"btn btn-success\" type=\"submit\" value=\"Save\">\n        </form>\n      </div>\n    </div><!-- /.modal-content -->\n  </div><!-- /.modal-dialog -->\n  </div><!-- /.modal -->\n\n\n</body>\n\n</html>\n"},"res-renderer.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0, maximum-scale=1.0\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>renderer properties</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH}}{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Renderer Properties</h4>\n        </div>\n        <div class=\"modal-body\">\n\n          <ol class=\"breadcrumb\">\n            <li class=\"active\">HOME</li>\n            {{#include \".\" \"parents\"}}\n              <li class=\"active\">{{name}}</li>\n            {{/include}}\n            <li>{{R.NAME}}</li>\n          </ol>\n\n          <p>Renderer will take following types into consideration</p>\n          <ol>\n            {{#if renderType}}\n            <li>renderType: {{renderType}}</li>\n            {{else}}\n            <li>renderType: <b>no type set</b></li>\n            {{/if}}\n            {{#if contentType}}\n            <li>contentType: mime/{{contentType}}</li>\n            {{/if}}\n            <li>resourceType: {{type}}</li>\n            {{#if superType}}\n            <li>superType: {{superType}}</li>\n            {{/if}}\n            <li>fallback: any/any</li>\n          </ol>\n\n          <h4>Set User Defined Renderers</h4>\n          <p>User defined renderer templates are stored in <a href=\"{{C.USER_TEMPLATES}}/{{renderType}}{{C.X}}res-list\">{{C.USER_TEMPLATES}}/{{renderType}}</a></p>\n\n          <div class=\"form-horizontal\">\n            <form method=\"post\" action=\"{{R.PATH}}\">\n              <div class=\"form-group\">\n                <label class=\"col-sm-2\">render type</label>\n                <div class=\"col-sm-8\">\n                  <select class=\"form-control\" name=\"_rt\">\n                    <option value=\"\">not found</option>\n                    <option disabled>-</option>\n                    {{#include C.USER_TEMPLATES \"rendertemplates\"}}\n                      <option value=\"{{this}}\" {{eq this ../renderType \"selected\"}}>{{this}}</option>\n                    {{/include}}\n                  </select>\n                </div>\n                <div class=\"col-sm-2\">\n                  <button class=\"btn btn-success\" type=\"submit\">\n                    <span>set</span>\n                  </button>\n                </div>\n              </div>\n            </form>\n            \n            <a class=\"btn btn-success\" href=\"{{C.USER_TEMPLATES}}{{C.X}}res-create\">Create New Template</a>\n          </div>\n\n      </div>\n      <div class=\"modal-footer\">\n\n        <form class=\"form-horizontal\" class=\"form-inline\" method=\"post\" id=\"save_form\" action=\"{{R.PATH}}{{C.X}}res-list\">\n          <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-list\">Cancel</a>\n          <input class=\"btn btn-success\" type=\"submit\" value=\"Save\">\n        </form>\n      </div>\n    </div><!-- /.modal-content -->\n  </div><!-- /.modal-dialog -->\n  </div><!-- /.modal -->\n\n\n</body>\n\n</html>\n"}},"notfound":{"res-list.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>Delete</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Resource Not Found</h4>\n        </div>\n        <div class=\"modal-body\">\n\n          <ol class=\"breadcrumb\">\n            <li><a href=\"/{{C.X}}res-list\">HOME</a></li>\n            {{#include \".\" \"parents\"}}\n              <li><a href=\"{{path}}{{C.X}}res-list\">{{name}}</a></li>\n            {{/include}}\n            <li class=\"active\">{{R.NAME}}</li>\n          </ol>\n\n          <h4>Resource\"{{name}} not found, create?\"</h4>\n\n        </div>\n        <div class=\"modal-footer\">\n          <form class=\"form-inline\" method=\"post\" action=\"{{R.PATH}}\">\n            <a class=\"btn btn-default\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\">Cancel</a>\n            <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH_APPEND}}{{C.X}}res-list\">\n            <input class=\"btn btn-success\" type=\"submit\" value=\"Create\">\n          </form>\n\n        </div>\n      </div><!-- /.modal-content -->\n    </div><!-- /.modal-dialog -->\n  </div><!-- /.modal -->\n\n</body>\n\n</html>\n\n"}},"root":{"res-create.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n  <title>mount</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n\n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Create Temportary Object Resource</h4>\n        </div>\n        <div class=\"modal-body\">\n\n          <ol class=\"breadcrumb\">\n            <li class=\"active\">HOME</a></li>\n            {{#include \".\" \"parents\"}}\n              <li class=\"active\">{{name}}</li>\n            {{/include}}\n            <li>{{R.NAME}}</li>\n          </ol>\n\n          <h6>Create ObjectResource as:</h6>\n\n          <div class=\"form-horizontal\">\n            <div class=\"form-horizontal\">\n              <form method=\"post\" action=\"{{R.PATH}}{:name}\">\n                <div class=\"form-group\">\n                  <div class=\"col-sm-8\">\n                    <input class=\"form-control\" name=\":name|newUUID\" value=\"\" autocomplete=\"off\" placeholder=\"name\">\n                    <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH}}{{C.X}}res-list\">\n                  </div>\n                  <div class=\"col-sm-2\">\n                    <button class=\"form-control btn btn-success\" type=\"submit\">\n                      <span class=\"glyphicon glyphicon-plus\"></span>\n                    </button>\n                  </div>\n                </div>\n              </form>\n            </div>\n          </div>\n\n        </div>\n        <div class=\"modal-footer\">\n          <a class=\"btn btn-default\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\">Cancel</a>\n        </div>\n      </div><!-- /.modal-content -->\n    </div><!-- /.modal-dialog -->\n  </div><!-- /.modal -->\n\n</body>\n\n</html>\n"},"res-list.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n  <title>Root</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n  <div class=\"container\">\n\n    <ol class=\"breadcrumb\">\n      <li><a href=\"/{{R.SUFFIX}}\">HOME</a></li>\n      {{#include \".\" \"parents\"}}\n        <li><a href=\"{{path}}{{R.SUFFIX}}\">{{name}}</a></li>\n      {{/include}}\n      <li class=\"active\">{{R.NAME}}</li>\n    </ol>\n\n    <div class=\"btn-group btn-group-sm\" style=\"margin-bottom:20px\">\n      <a class=\"btn btn-default\" href=\"{{C.USER_TEMPLATES}}{{C.X}}res-create\"><span class=\"glyphicon glyphicon-fire\"></span> Create Template</a>\n      <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-create\"><span class=\"glyphicon glyphicon-hdd\"></span> Create Temporary Resource</a>\n    </div>\n\n    <table class=\"table ui-dropzone\">\n      {{#include \".\" \"children\"}}\n\n        {{#if isContentResource}}\n          <tr>\n            <td>\n              <i class=\"glyphicon glyphicon-file\"></i> <a href=\"{{path}}{{C.X}}edit\">{{name}}</a>\n            </td>\n            <td>\n              {{renderType}}\n            </td>\n            <td>\n              <div class=\"btn-group btn-group-xs pull-right\">\n                <a class=\"btn btn-default glyphicon glyphicon-download-alt\" href=\"{{path}}{{C.X}}res-exportto/\"></a>\n              </div>\n            </td>\n          </tr>\n        {{else}}\n          <tr>\n            <td>\n              <i class=\"glyphicon glyphicon-folder-close\"></i> <a href=\"{{path}}{{R.SUFFIX}}\">{{name}}</a>\n            </td>\n            <td>\n              {{#if renderType}}\n                <a href=\"{{R.PATH}}/{{name}}{{C.X}}res-renderer\">{{renderType}}</a>\n              {{else}}\n                {{type}}\n              {{/if}}\n            </td>\n            <td>\n              <div class=\"btn-group btn-group-xs pull-right\">\n                <a class=\"btn btn-default glyphicon glyphicon-download-alt\" href=\"{{path}}{{C.X}}res-exportto/\"></a>\n              </div>\n            </td>\n          </tr>\n        {{/if}}\n\n      {{/include}}\n    </table>\n\n  </div>\n</body>\n\n</html>\n"}},"templates":{"rendertemplates.js":{"_ct":"text/plain","_pt":"resource/content","_content":"(function (res, writer, context) {\n  writer.start('object/javascript');\n  var types = [];\n\n  Tools.visitAllChidren(res, false, function (path, res) {\n    if (path) {\n      if (res.isContentResource()) {\n        var name = Utils.filename(path);\n        var dir = Utils.filename_dir(path).substr(1);\n        if (!types.includes(dir)) types.push(dir);\n      }\n    }\n    else {\n      writer.write(types.sort());\n      writer.end();\n    }\n  });\n\n})\n"},"res-create.hbs":{"_ct":"text/plain","_pt":"resource/content","_content":"<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n  <link rel=\"icon\" href=\"data:;base64,iVBORw0KGgo=\">\n\n  <title>create</title>\n\n  <link href=\"{{C.BOOTSTRAP_CSS}}\" rel=\"stylesheet\">\n</head>\n\n<body>\n  \n  <div role=\"dialog\">\n    <div class=\"modal-dialog\" role=\"document\">\n      <div class=\"modal-content\">\n        <div class=\"modal-header\">\n          <a class=\"close\" href=\"{{R.PATH_APPEND}}../{{C.X}}res-list\"><span aria-hidden=\"true\">&times;</span></a>\n          <h4 class=\"modal-title\">Create New Template</h4>\n        </div>\n        <div class=\"modal-body\">\n\n    <ol class=\"breadcrumb\">\n      <li class=\"active\">HOME</li>\n      {{#include \".\" \"parents\"}}\n        <li class=\"active\">{{name}}</li>\n      {{/include}}\n      <li class=\"active\">{{R.NAME}}</li>\n    </ol>\n\n    <h6>create renderer:</h6>\n\n        <div>\n          <p>\n            Renderer templates are resolved by scanning for a file matching specific naming convention pattern.\n          <p>\n            Pattern is composed of these parts: <br>\n            <span class=\"bg-info\">resource type</span> - usually a directory<br>\n            <span class=\"bg-success\">selector</span> - \"default\" or what gets appended after \"{{C.X}}\" like \"json\" for {{C.X}}json\n          <p>\n            There are two pattern variants you can use, for example:\n          <ul>\n            <li><span class=\"bg-info\">web/mycomponent</span><b>/</b><span class=\"bg-success\">default</span></li>\n            <li><span class=\"bg-info\">web/mycomponent</span>/mycomponent<b>.</b><span class=\"bg-success\">default</span></li>\n          </ul>\n        </div>\n\n    <div class=\"form-horizontal\">\n      <form method=\"post\" action=\"{{R.PATH}}/{:name}.{:ext}\">\n        <div class=\"form-group\">\n            <div class=\"col-sm-3\">\n          <select name=\":ext\" class=\"form-control\">\n            <option value=\"hbs\">\n              handlebars\n            </option>\n            <option value=\"js\">\n              javascript\n            </option>\n          </select>\n          </div>\n\n\n            <div class=\"col-sm-7\">\n          <input class=\"form-control\" name=\":name\" value=\"{{R.DATA_PATH}}\" placeholder=\"full template file path\" required=\"\" autocomplete=\"off\">\n          <input type=\"hidden\" name=\"_content\" value=\"\"> \n          <input type=\"hidden\" name=\"_rt\" value=\"resource/content\"> \n          <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH}}/{:name}.{:ext}{{C.X}}edit\">\n          </div>\n\n            <div class=\"col-sm-2\">\n                <button class=\"btn btn-success\" type=\"submit\" value=\"add\">\n                  <span class=\"glyphicon glyphicon-plus\"></span>\n                </button>\n                </div>\n        </div>\n      </form>\n    </div>\n\n    <h6>create directory:</h6>\n\n      <div class=\"form-horizontal\">\n        <form method=\"post\" action=\"{{R.PATH}}/{:name}\">\n          <div class=\"form-group\">\n            <div class=\"col-sm-8\">\n              <input class=\"form-control\" name=\":name\" value=\"\" required=\"\" autocomplete=\"off\"> <input type=\"hidden\" name=\":forward\" value=\"{{R.PATH}}{{C.X}}res-list\">\n            </div>\n\n\n            <div class=\"col-sm-2\">\n                <button class=\"btn btn-success\" type=\"submit\" value=\"add\">\n                  <span class=\"glyphicon glyphicon-plus\"></span>\n                </button>\n            </div>\n          </div>\n        </form>\n    </div>\n\n        </div>\n        <div class=\"modal-footer\">\n          <a class=\"btn btn-default\" href=\"{{R.PATH}}{{C.X}}res-list\">Cancel</a>\n        </div>\n      </div><!-- /.modal-content -->\n    </div><!-- /.modal-dialog -->\n  </div><!-- /.modal -->\n\n</body>\n\n</html>\n"}}}}
